"use strict";function activateNavButton(){var t=document.querySelector(".nav-button"),e=document.querySelector(".nav-bar");t.addEventListener("click",function(){toggleClass(e,"hide-nav-bar")}),t.addEventListener("keyup",function(t){13===t.keyCode&&toggleClass(e,"hide-nav-bar")})}function activateStickyNav(){var t=document.querySelector(".grid-nav"),e=document.querySelector("main"),n=t.offsetTop;window.pageYOffset>=n?(t.classList.add("sticky"),e.classList.add("anti-sticky")):(t.classList.remove("sticky"),e.classList.remove("anti-sticky"))}function activateNavBar(){for(var i=document.querySelectorAll(".nav-item-main"),t=function(t){var e=i[t],n=e.parentNode;e.addEventListener("click",function(t){n.children[1].classList.toggle("nav-hidden-links"),hideItemsOnClickElsewhere(n)})},e=0;e<i.length;e++)t(e)}function hideItemsOnClickElsewhere(i){document.addEventListener("click",function(t){var e=i.children[0],n=i.children[1];t.target!==e&&n.classList.add("nav-hidden-links")})}function toggleClass(t,e){if(NodeList.prototype.isPrototypeOf(t))for(var n=0;n<t.length;n++)t[n].classList.toggle(e);else t.classList.toggle(e)}document.addEventListener("DOMContentLoaded",function(){activateNavButton(),activateNavBar()},!1),window.onscroll=function(){activateStickyNav()};